import pymysql

db = pymysql.connect("localhost", "root", "", "testdb")

c = db.cursor()

c.execute("drop table if exists MobileAccelerometer")

c.execute("""CREATE TABLE MobileAccelerometer(
	date TEXT,
	max FLOAT,
	may FLOAT,
	maz FLOAT,
	activity TEXT
	)""")
print ("Table created successfully")
db.close()
	
