var util = require('util');
var bleno = require('..');

var WriteOnlyCharacteristic = require('./WriteOnlyCharacteristic');
var NotifyOnlyCharacteristic = require('./NotifyOnlyCharacteristic');

function BlueService(){
    bleno.PrimaryService.call(this, {
        uuid: '7069',
        characteristics:[
            new WriteOnlyCharacteristic(),
            new NotifyOnlyCharacteristic()
        ]
    });
}

util.inherits(BlueService, bleno.PrimaryService);

module.exports = BlueService;
